import { Component } from '@angular/core';
import { isLoading$ } from 'projects/core/src/lib/services/loading.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  loading$ = isLoading$;
  title = 'PizzeriaFrontEnd';
  condition = false;
}
<h1>Buenos días</h1>
<cap-spinner *ngIf="loading$ | async"></cap-spinner>
<button (click)="condition=true">Encender Spinner</button>
<lib-button></lib-button>
<router-outlet></router-outlet>
